
import 'package:ar_zoo_explorers/flavors.dart';
import 'package:injectable/injectable.dart';

@singleton
class AppConfig {
  Flavor? get _flavor => F.appFlavor;
  String _packageName = "";


  String get packageName => _packageName;

  String get appName {
    return F.title;
  }

  String get storeCredentials {
    return "";
  }

  String get termOfServiceLink => "https://www.pinterest.com/pin/501447739774161315/";
  String get privacyPolicyLink => "https://www.freeprivacypolicy.com/live/c1992584-e436-4ccb-804a-e162304b44b7";

  String get supportedEmail => "newsbapps@gmail.com";

  bool get defaultPremium {
    if (_flavor == Flavor.DEVELOP) {
      return false;
    }
    return false;
  }
}
